/* @flow */

import React, { PureComponent } from 'react';

function getStyles(props) {
  const { checked } = props;

  const styles = {
    container: {
      padding: '12px',
      boxSizing: 'border-box',
      width: '48px',
      height: '48px',
      display: 'inline-flex',
      cursor: 'pointer',
      position: 'relative',
      zIndex: '10',
      justifyContent: 'center',
      alignItems: 'center',
      // alignSelf: 'top',
    },
    outline: {
      boxSizing: 'border-box',
      width: '18px',
      height: '18px',
      margin: '0px',
      cursor: 'pointer',
      overflow: 'hidden',
      border: `2px solid rgba(${ checked ? '63,81,181,1' : '0,0,0, 0.54'})`,
      borderRadius: '2px',
      zIndex: '2',
    },
    tick: {
      height: '100%',
      width: '100%',
      background: 'transparent',
      // transitionDuration: '0.28s',
      // transitionTimingFunction: 'cubic-bezier(0.4, 0, 0.2, 1)',
      // transitionProperty: 'background',
      transform: checked ? 'scale(1)' : 'scale(0)',
      transition: 'transform 200ms cubic-bezier(0.4, 0, 0.2, 1)',
      background: checked && 'rgb(63,81,181) url("data:image/svg+xml;base64,")',
      // background: 'rgb(63,81,181) url("data:image/svg+xml;base64,")',

    },

  }
  return styles;
};
export default class CssCheckbox extends PureComponent {
  constructor() {
    super();

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    if (this.props.onClick) {
      this.props.onClick(e);
    }
  }
  render() {
    const style = getStyles(this.props);
    return (
      <div style={style.container}
        onClick={this.handleClick}
      >
        <div style={style.outline}>
          <div style={style.tick} />
        </div>
      </div>
    );
  }
};